CREATE TABLE `newsletter_registrations` ( `eMailAddress` VARCHAR(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL , `code` VARCHAR(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL , `activateUntil` DATE NULL , PRIMARY KEY (`eMailAddress`)) ENGINE = InnoDB;
UPDATE `strings` SET `de` = '-Seite und aktiviere dort den Erhalt von Newslettern unter der Kategorie \"Kontaktdaten\".' WHERE `strings`.`identifier` = 'account_allowNewsletter_account_notice_suffix';
