INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('navigation_newsletter_subscribe', 'Subscribe to newsletter', 'Newsletter abonnieren');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_account_notice_prefix', 'If you already have an app account registered on your e-mail address, you have to log in, go to your ', 'Wenn du bereits einen App-Account auf deiner E-Mail-Adresse registriert hast, dann logge dich ein, gehe auf deine ');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_account_notice_suffix', ' page and activate the receipt of newsletters there.', '-Seite und aktivere den Erhalt von Newslettern dort.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_registration_confirm_sent', 'An e-mail with a confirmation link has been sent to the specified e-mail address. The newsletter subscription is only effective after opening the link.', 'Eine E-Mail mit Bestätigungslink wurde an die angegebene E-Mail-Adresse versandt. Erst nach dem Aufrufen des Links ist die Anmeldung zu den Newslettern wirksam.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_registration_confirmed', 'The receipt of newsletters has been enabled.', 'Der Erhalt von Newslettern wurde aktiviert.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_registration_canceled', 'The receipt of newsletters has been disabled.', 'Der Erhalt von Newslettern wurde eingestellt.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_code_invalid', 'The provided code is not valid (anymore).', 'Der angegebene Code ist nicht (mehr) gültig.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_registration_account', 'There is already an app account registered on the provided e-mail address. See the note below for more information on how to subscribe to newsletters with an app account.', 'Auf die angegebene E-Mail-Adresse ist bereits ein App-Account registriert. Beachte den Hinweis weiter unten, der erklärt, wie der Erhalt von Newslettern per App-Account aktiviert werden kann.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('account_allowNewsletter_registration_already', 'The provided e-mail address is already enlisted for newsletter receipt.', 'Die angegebene E-Mail-Adresse ist bereits für den Empfang von Newslettern registriert.');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('mail_confirm_newsletter_title', 'Junges Münsterschwarzach App - Newsletter activation', 'Junges Münsterschwarzach App - Newsletter-Aktivierung');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('mail_confirm_newsletter_message_paragraph_1', 'Hello there,<br/><br/>open the following link to confirm your recently requested newsletter receipt:<br/><br/>', 'Hallo,<br/><br/>um den kürzlich angefragten Erhalt von Newslettern zu bestätigen, rufe bitte folgenden Link auf:<br/><br/>');
INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('mail_confirm_newsletter_message_paragraph_2', '<br/><br/>If you were not able to open the link, you should copy it into your browser\'s address bar and open it there.<br/>This link is valid for 7 days. After expiration, your newsletter registration process will be lost and you have to register for newsletter receipt again.<br/>If you did not wish to receive newsletters from us, you could ignore this e-mail without any concerns.<br/><br/>', '<br/><br/>Solltest du den Link nicht öffnen können, dann kopiere ihn in die Addresszeile deines Browsers und rufe ihn dort auf.<br/>Dieser Link ist 7 Tage lang gültig. Danach wird die Newsletter-Registrierung abgebrochen und du musst dich erneut für Newsletter registrieren.<br/>Wenn du keine Newsletter von uns empfangen möchtest, kannst du diese E-Mail unbesorgt ignorieren.<br/><br/>');
