INSERT INTO `strings` (`identifier`, `en`, `de`) VALUES ('mail_confirm_activation_message_paragraph_enrollment', '<strong>Please pay attention that your event enrollment is only valid, after you have activated your account. If the link is already expired, please register again.</strong><br/><br/>', '<strong>Beachte bitte, dass deine Kursanmeldung erst gültig ist, nachdem du deinen Account aktiviert hast. Sollte der Link abgelaufen sein, dann registriere dich bitte erneut.</strong><br/><br/>');
