INSERT INTO `strings` (`identifier`, `de`, `en`, `mode`) VALUES ('legal_notice_authorized_representative_heading', 'Vertretungsberechtigter', 'tbd', 'ANDROID+IOS+WEB'), ('legal_notice_authorized_representative_paragraph', 'Abt Michael Reepen - Abtei Münsterschwarzach\r\nSchweinfurter Straße 40\r\n97359 Münsterschwarzach\r\nTelefon: Tel. 09324 200\r\nE-Mail: prokura@abtei-muensterschwarzach.de', 'tbd', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `de`, `en`, `mode`) VALUES ('legal_notice_responsible_content_heading', 'Inhaltlich verantwortlich', 'tbd', 'ANDROID+IOS+WEB'), ('legal_notice_responsible_content_paragraph', 'Thomas Tribula\r\n(Betreiber, Redakteur und Moderator)\r\nE-Mail: tribulathomas@hotmail.com\r\n\r\nJulia Martin\r\n(Redakteurin und Moderatorin)\r\nE-Mail: j.martin@abtei-muensterschwarzach.de', 'tbd', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `de`, `en`, `mode`) VALUES ('legal_notice_responsible_technics_heading', 'Technisch verantwortlich', 'tbd', 'ANDROID+IOS+WEB'), ('legal_notice_responsible_technics_paragraph', 'Lucas Kinne\r\n(Betreiber, Entwickler und Support)\r\nE-Mail: lucas.kinne@live.de', 'tbd', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos', 'tbd', 'Nutzungsbedingungen', 'ANDROID+IOS+WEB'), ('tos_general', 'tbd', 'Allgemeines', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_general_compliance_heading', 'tbd', 'Einhaltung', 'ANDROID+IOS+WEB'), ('tos_general_compliance_paragraph', 'tbd', 'Mit dem Akzeptieren unserer Nutzungsbestimmungen bestätigen Sie, diese vollständig gelesen zu haben und zu befolgen.\r\nVerstöße können Sanktionen zur Folge haben. Mit Verstößen in Zusammenhang stehende Beiträge auf unserer Plattform werden geändert oder gelöscht. Verstöße gegen geltendes deutsches Recht werden außerdem umgehend zur Anzeige gebracht.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_general_validity_heading', 'tbd', 'Gültigkeit', 'ANDROID+IOS+WEB'), ('tos_general_validity_paragraph', 'tbd', 'Sollten einzelne Punkte unserer Nutzungsbestimmungen nicht wirksam oder undurchführbar sein, beeinträchtigt das nicht die Gültigkeit der wirksamen Teile.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_general_householders_heading', 'tbd', 'Hausrecht', 'ANDROID+IOS+WEB'), ('tos_general_householders_paragraph', 'tbd', 'Wir behalten uns das Hausrecht vor.\r\nEin Account kann deshalb durch uns ohne Angabe von Gründen gesperrt oder gelöscht werden. Ebenso können wir Beiträge ohne Angabe von Gründen entfernen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_accounts', 'tbd', 'Accounts', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_accounts_membership_heading', 'tbd', 'Mitgliedschaft', 'ANDROID+IOS+WEB'), ('tos_accounts_membership_paragraph', 'tbd', 'Jeder Person ist es gestattet, einen Account bei uns zu besitzen.\r\nDie Nutzung unserer Plattform ist für Personen, die das 16. Lebensjahr noch nicht vollendet haben, verboten. Eine Ausnahme liegt vor, wenn die betreffende Person die Zustimmung ihrer Eltern oder Erziehungsberechtigten besitzt.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_accounts_costs_heading', 'tbd', 'Kosten', 'ANDROID+IOS+WEB'), ('tos_accounts_costs_paragraph', 'tbd', 'Die Account-Erstellung und Nutzung all unserer Dienste ist kostenlos.\r\nSie haben als Nutzer aus diesem Grund zu keiner Zeit Schadensersatzansprüche an uns.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_accounts_security_heading', 'tbd', 'Sicherheit', 'ANDROID+IOS+WEB'), ('tos_accounts_security_paragraph', 'tbd', 'Jede Person ist für ihren Account selbst verantwortlich. Wir können bei Account-Verlust nur reagieren, wenn ausreichend Beweise vorgelegt werden können, dass man der Besitzer des besagten Accounts ist. Dies ist in den meisten Fällen das Innehaben der zum Account gehörenden E-Mail-Adresse.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents', 'tbd', 'Inhalte', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_language_heading', 'tbd', 'Sprache', 'ANDROID+IOS+WEB'), ('tos_contents_language_paragraph', 'tbd', 'Auf unseren Plattformen wird ausschließlich auf Deutsch oder Englisch kommuniziert.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_behavior_heading', 'tbd', 'Verhalten und Ausdrucksweise', 'ANDROID+IOS+WEB'), ('tos_contents_behavior_paragraph', 'tbd', 'Auf unseren Plattformen sind Inhalte folgender Natur verboten:\r\n• Spam\r\n• Beleidigung\r\n• Anprangerung\r\n• Diskriminierung\r\n• Gewaltverherrlichung\r\n• Rechtsradikalismus\r\n• Propaganda und extreme politische Ansichten\r\n• Pornographie und Obszönitäten\r\n\r\nWir setzen außerdem zur Wahrung eines gepflegten Klimas einen respektvollen und möglichst freundlichen Umgang miteinander voraus.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_disturbance_heading', 'tbd', 'Unruhestifung', 'ANDROID+IOS+WEB'), ('tos_contents_disturbance_paragraph', 'tbd', 'Accounts, die offenkundig zum Zweck der Unruhestiftung erstellt wurden, werden kommentarlos gesperrt.\r\nMitglieder, die nachhaltig und wiederholt negativ auffallen, indem sie den Frieden in der Community häufig stören, erhalten eingeschränkte Rechte bis hin zur Account-Sperrung.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_copyright_heading', 'tbd', 'Urheberrecht', 'ANDROID+IOS+WEB'), ('tos_contents_copyright_paragraph', 'tbd', 'Texte, Bilder, Grafiken sowie die Gestaltung der Plattformen können unter Umständen dem Urheberrecht unterliegen.\r\n\r\nNicht urheberrechtlich geschützt sind allerdings nach § 5 des Urheberrechtsgesetz (UrhG):\r\n• Gesetze, Verordnungen, amtliche Erlasse und Bekanntmachungen sowie Entscheidungen und amtlich verfasste Leitsätze zu Entscheidungen und\r\n• andere amtliche Werke, die im amtlichen Interesse zur allgemeinen Kenntnisnahme veröffentlicht worden sind, mit der Einschränkung, dass die Bestimmungen über änderungsverbot und Quellenangabe in § 62 Abs. 1 bis 3 und § 63 Abs. 1 und 2 UrhG entsprechend anzuwenden sind.\r\n\r\nPrivatpersonen dürfen grundsätzlich urheberrechtlich geschütztes Material zum privaten und sonstigen eigenen Gebrauch im Rahmen des § 53 UrhG verwenden. Eine Vervielfältigung oder Verwendung urheberrechtlich geschützten Materials der Plattformen oder Teilen davon ist in anderen elektronischen oder gedruckten Publikationen und deren Veröffentlichungen nur mit Einwilligung des Betreibers gestattet. Der Nachdruck und die Auswertung sind allgemein gestattet, sofern die Quelle bezeichnet wird.\r\n\r\nTexte, Bilder, Grafiken und sonstige Dateien können ganz oder teilweise dem Urheberrecht Dritter unterliegen. Über das Bestehen möglicher Rechte Dritter geben die für den Inhalt Verantwortlichen nähere Auskünfte, sofern diese Rechte nicht bereits durch entsprechende Hinweise auf der Webseite kenntlich gemacht sind.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_advertising_heading', 'tbd', 'Werbung', 'ANDROID+IOS+WEB'), ('tos_contents_advertising_paragraph', 'tbd', 'Werbung für andere Apps, Webseiten, Produkte oder Inhalte kommerzieller Art ist verboten.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_contents_fraud_heading', 'tbd', 'Betrug', 'ANDROID+IOS+WEB'), ('tos_contents_fraud_paragraph', 'tbd', 'Betrug wird auf unserer Plattform nicht geduldet.\r\nWir werden Sie niemals nach Ihrem Passwort fragen. Sollte es dazu kommen, handelt es sich um einen Betrüger. Ein solcher Vorfall sollte schnellstens an uns gemeldet werden.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_infringements', 'tbd', 'Verstöße', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_infringements_duty_heading', 'tbd', 'Meldepflicht', 'ANDROID+IOS+WEB'), ('tos_infringements_duty_paragraph', 'tbd', 'Verstößt ein Mitglied unserer Community gegen die Nutzungsbestimmungen, so ist dieses Mitglied mit entsprechenden Beweisen (Screenshots oder ähnlichem) bei uns zu melden.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_infringements_faults_heading', 'tbd', 'Systemfehler', 'ANDROID+IOS+WEB'), ('tos_infringements_faults_paragraph', 'tbd', 'Falls auf unserer Plattform Sicherheitslücken oder andere Fehler bemerkt werden, durch die bestimmte Benutzer unrechtmäßige Vorteile oder Berechtigungen erlangen, besteht eine umgehende Meldepflicht gegenüber dem Betreiber. Das Ausnutzen solcher Fehler ist strengstens verboten.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('tos_infringements_sanctions_heading', 'tbd', 'Sanktionen', 'ANDROID+IOS+WEB'), ('tos_infringements_sanctions_paragraph', 'tbd', 'Sanktionen erfolgen nur bei ausreichendem Beweismaterial. Der Betroffene hat das Recht, das vorliegende Beweismaterial einzusehen und gegebenenfalls Beschwerde einzulegen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('legal_disclaimer_heading', 'tbd', 'Haftungsbeschränkung und -ausschluss', 'ANDROID+IOS+WEB'), ('legal_disclaimer_paragraph', 'tbd', 'Der Betreiber hat alle auf den Plattformen bereitgestellten Informationen nach bestem Wissen und Gewissen erarbeitet und geprüft. Es wird jedoch keine Gewähr für die Aktualität, Richtigkeit, Vollständigkeit oder Qualität und jederzeitige Verfügbarkeit der bereit gestellten Informationen übernommen.\r\nUnbeschadet der Regelungen des § 675 Absatz 2 BGB gilt für das bereitgestellte Informationsangebot folgende', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('legal_disclaimer_limitation_heading', 'tbd', 'Haftungsbeschränkung', 'ANDROID+IOS+WEB'), ('legal_disclaimer_limitation_paragraph', 'tbd', 'Der Betreiber haftet nicht für Schäden, die durch die Nutzung oder Nichtnutzung der in ihrem Angebot enthaltenen Informationen entstehen. Der Haftungsausschluss gilt nicht, soweit die Vorschrift des § 823 BGB (Haftung bei vorsätzlicher oder fahrlässiger Schadenszufügung) einschlägig ist.\r\nFür etwaige Schäden, die durch Malware oder andere Software verursacht werden, wird nicht gehaftet. Namentlich gekennzeichnete Seiten geben die Auffassungen und Erkenntnisse der unterzeichnenden Personen wieder.\r\nDer Betreiber behält es sich ausdrücklich vor, einzelne Teile der Plattform oder das gesamte Angebot ohne gesonderte Ankündigung zu verändern, zu ergänzen, zu löschen oder die Veröffentlichung zeitweise oder endgültig einzustellen. ', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('legal_disclaimer_references_heading', 'tbd', 'Haftungsausschluss für Verweise auf andere Internetangebote', 'ANDROID+IOS+WEB'), ('legal_disclaimer_references_paragraph', 'tbd', 'Der Betreiber ist nur für die eigenen Inhalte, die es zur Nutzung bereithält, nach den einschlägigen Gesetzen verantwortlich. Von diesen eigenen Inhalten sind Querverweise (Links) auf die Internetangebote anderer Anbieter zu unterscheiden. Durch den als Link gekennzeichneten Querverweis wird der Zugang zu fremden Inhalten ermöglicht. Wir vermitteln lediglich den Zugang zur Nutzung dieser Inhalte durch den Querverweis. Für die fremden Inhalte sind wir nicht verantwortlich, da wir die Übermittlung der Information nicht veranlassen, den Adressaten der übermittelten Informationen nicht auswählen und die übermittelten Informationen auch nicht ausgewählt oder verändert haben; es erfolgt auch keine automatische kurzzeitige Zwischenspeicherung dieser fremden Informationen. \r\n\r\nFür illegale, fehlerhafte oder unvollständige Inhalte und insbesondere für Schäden, die aus der Nutzung oder Nichtnutzung von Informationen Dritter entstehen, haftet allein der jeweilige Anbieter des Internetangebots, auf welches verwiesen wurde.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('legal_disclaimer_references_paragraph_2', 'tbd', 'Gegen Verweise von fremden Internetangeboten auf das vorliegende Internetangebot bestehen keine Einwände.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_heading', 'tbd', 'Datenschutzerklärung', 'ANDROID+IOS+WEB'), ('privacy_policy_paragraph', 'tbd', 'Wir freuen uns darüber, dass Sie sich entschieden haben, unsere Plattformen zu nutzen, und über Ihr Interesse an unseren Dienstleistungen.\r\nDer Schutz Ihrer persönlichen Daten ist uns ein ernstes Anliegen. In allen deutschen kirchlichen Dienststellen gelten für die Erhebung, Verarbeitung und Nutzung personenbezogener Daten die Bestimmungen des Gesetzes über den kirchlichen Datenschutz (KDG) sowie die sonstigen anzuwendenden kirchlichen und staatlichen Datenschutzvorschriften.\r\nMit dieser Datenschutzerklärung möchten wir Sie über die Art, den Umfang und Zweck der Erhebung und Verwendung personenbezogener Daten auf unseren Plattformen informieren.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_responsibility_heading', 'tbd', 'Wer ist verantwortlich für die Datenverarbeitung?', 'ANDROID+IOS+WEB'), ('privacy_policy_data_responsibility_paragraph', 'tbd', 'Die Datenverarbeitung auf den Plattformen erfolgt durch den Betreiber. Dessen Kontaktdaten können Sie diesem Impressum entnehmen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_legal_foundation_heading', 'tbd', 'Auf welcher rechtlichen Grundlage basiert die Datenverarbeitung?', 'ANDROID+IOS+WEB'), ('privacy_policy_legal_foundation_paragraph', 'tbd', 'Für die Nutzung unserer Plattformen ist es grundsätzlich nicht erforderlich, dass Sie uns personenbezogene Daten mitteilen, es sei denn, Sie möchten bestimmte Angebote von uns nutzen, wie z.B. das Anmelden für Veranstaltungen oder die Kommunikation mit anderen Mitgliedern unserer virtuellen Gemeinschaft. In diesem Fall ist es erforderlich, dass Sie bestimmte Daten angeben. Durch die Angabe der benötigten Daten erklären Sie sich automatisch damit einverstanden, dass wir diese Daten verarbeiten dürfen.\r\n\r\nDie Nutzung der angebotenen Dienstleistungen seitens des Nutzers erfolgt ausdrücklich auf freiwilliger Basis. \r\n\r\nPersonenbezogene Daten werden von uns nur dann erhoben, genutzt und gegebenenfalls weitergegeben, wenn dies gesetzlich erlaubt ist. Auf keinen Fall werden die Daten in Gebiete außerhalb der EU transferiert. Eine gesetzliche Erlaubnis zu einer entsprechenden Verarbeitung basiert auf Art. 6 der EU-Datenschutz-Grundverordnung, wenn:\r\n• die betroffene Person ihre Einwilligung zu der Verarbeitung der sie betreffenden personenbezogenen Daten für einen oder mehrere bestimmte Zwecke gegeben hat\r\n• die Verarbeitung für die Erfüllung eines Vertrags, dessen Vertragspartei die betroffene Person ist, oder zur Durchführung vorvertraglicher Maßnahmen erforderlich ist, die auf Anfrage der betroffenen Person erfolgen\r\n• die Verarbeitung zur Erfüllung einer rechtlichen Verpflichtung erforderlich ist, der der Verantwortliche unterliegt\r\n• die Verarbeitung erforderlich ist, um lebenswichtige Interessen der betroffenen Person oder einer anderen natürlichen Person zu schützen\r\n• die Verarbeitung für die Wahrnehmung einer Aufgabe erforderlich ist, die im öffentlichen Interesse liegt oder in Ausübung öffentlicher Gewalt erfolgt, die dem Verantwortlichen übertragen wurde\r\n• die Verarbeitung zur Wahrung der berechtigten Interessen des Verantwortlichen oder eines Dritten erforderlich ist, sofern nicht die Interessen oder Grundrechte und Grundfreiheiten der betroffenen Person, die den Schutz personenbezogener Daten erfordern, überwiegen, insbesondere dann, wenn es sich bei der betroffenen Person um ein Kind handelt', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_cause_heading', 'tbd', 'Warum benötigen wir Ihre Daten?', 'ANDROID+IOS+WEB'), ('privacy_policy_data_cause_paragraph', 'tbd', 'Daten werden zum einen erhoben, um die Benutzerfreundlichkeit zu erhöhen. Anhand der Sprache des Betriebssystems ist es uns beispielsweise möglich, aus den verfügbaren Übersetzungen der Web-App die für Sie optimale Sprache zu bestimmen.\r\nAndere Daten benötigen wir, um Ihnen unsere angebotenen Dienstleistungen zur Verfügung stellen zu können. Damit Sie sich zum Beispiel für eine unserer Veranstaltungen anmelden können, müssen wir wissen, wer Sie sind.\r\nWieder andere Daten, wie Ihre IP-Adresse, werden aus technischen Gründen von unserer Infrastruktur gesammelt, was wir nicht beeinflussen können.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_heading', 'tbd', 'Welche Daten werden erhoben?', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_paragraph', 'tbd', 'Ihre Daten werden auf verschiedene Weisen erhoben.\r\nManche Daten werden bei der Ausführung der Web-App oder der Kommunikation zwischen Web-App und zugehörigem Web-Service erfasst. Dabei handelt es sich hauptsächlich um technische Daten, die automatisch erhoben werden.\r\nAndere Daten werden dadurch erhoben, dass Sie uns diese mitteilen. Hierbei kann es sich zum Beispiel um persönliche Daten handeln, die Sie in ein Formular eingeben, oder um Ihre Einwilligungen, die Sie uns gegeben haben.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_technical', 'tbd', 'Technische Daten', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_technical_cookies_heading', 'tbd', 'Cookies', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_technical_cookies_paragraph', 'tbd', 'Die meisten Browser sind so eingestellt, dass sie die Verwendung von Cookies akzeptieren. Diese Funktion kann aber durch die Einstellung des Internet-Browsers von Ihnen abgeschaltet werden. Sie können Ihren Internet-Browser so einstellen, dass dieser entweder alle Cookies automatisch akzeptiert, alle Cookies automatisch ablehnt oder Sie bei jedem Cookie fragt, ob Sie es akzeptieren oder ablehnen möchten.\r\nWir weisen Sie jedoch darauf hin, dass Sie bei deaktivierten Cookies gegebenenfalls nicht alle Funktionen dieser Website nutzen können.\r\n\r\nDie Speicherung der Cookies erfolgt auf Grundlage von Art. 6 Abs. 1 lit. f DSGVO.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_technical_cookies_session_heading', 'tbd', 'Session-Cookies', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_technical_cookies_session_paragraph', 'tbd', 'Unsere Webseite zur Moderation der Web-App verwendet sogenannte \"Session-Cookies\".\r\nSobald Sie sich eingeloggt haben, werden Ihre Sitzungsdaten (Anzeigename, Berechtigungsstufe und ähnliches) als Sitzungsobjekt auf dem Server gespeichert. Um bei Ihrem nächsten Seitenaufruf die Sitzung aufrechterhalten zu können, wird eine Referenz zu dem serverseitigen Sitzungsobjekt in Form einer Textdatei (Cookie) auf Ihrem Rechner gespeichert. Beim nächsten Seitenaufruf sendet Ihr Internet-Browser den Cookie automatisch mit. Der Session-Cookie sammelt demzufolge keine Daten, sondern dient lediglich als Zwischenablage für Ihre Authentisierung.\r\nEr wird automatisch gelöscht, sobald Sie Ihren Internet-Browser schließen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_technical_cookies_persistent_heading', 'tbd', 'Persistent-Cookies', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_technical_cookies_persistent_paragraph', 'tbd', 'Bei der Nutzung der Web-App oder der zugehörigen Moderations-Website können unter Umständen weitere Cookies verwendet werden, wie zum Beispiel zur Ablage von Sitzungsinformationen. Sie dienen lediglich der Ablage von Daten und werden nicht verwendet, um Sie zu verfolgen, Nutzerprofile zu erstellen oder personalisierte Werbung zu generieren.\r\nDiese Cookies bleiben erhalten, bis Sie sie löschen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_technical_log_heading', 'tbd', 'Log-Dateien', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_technical_log_paragraph', 'tbd', 'Bei jeder Anforderung einer Internetdatei werden folgende Zugriffsdaten erhoben und in sogenannten Server-Log-Dateien gespeichert:\r\n• die Seite, von der aus die Datei angefordert wurde \r\n• der Name der aufgerufenen Datei \r\n• das Datum und die Uhrzeit der Anforderung \r\n• die übertragene Datenmenge \r\n• der Zugriffsstatus (Datei übertragen, Datei nicht gefunden, etc.) \r\n• die Beschreibung des verwendeten Webbrowsertyps bzw. des verwendeten Betriebssystems \r\n• die IP-Adresse des anfordernden Rechners \r\n• die zuletzt vom anfordernden Rechner besuchte Website (Referrer URL)\r\n\r\nDie erhobenen Daten werden automatisch vom Web-Browser an uns übermittelt und ausschließlich anonymisiert zu technischen oder statistischen Zwecken verwendet; Ein Abgleich oder das Zusammenführen mit anderen Datenbeständen oder eine Weitergabe an Dritte, auch in Auszügen, findet nicht statt.\r\n\r\nGrundlage für die Datenverarbeitung ist Art. 6 Abs. 1 lit. b DSGVO, der die Verarbeitung von Daten zur Erfüllung eines Vertrags oder vorvertraglicher Maßnahmen gestattet.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_personal', 'tbd', 'Persönliche Daten', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_personal_account_heading', 'tbd', 'Account-Daten', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_personal_account_paragraph', 'tbd', 'Diese Kategorie umfasst die E-Mail-Adresse, das Passwort (gespeichert als Hash) und den Anzeigenamen.\r\n\r\nUm einige unserer angebotenen Dienste, die auf der Kommunikation zwischen Nutzern basieren, nutzen zu können, benötigen Sie einen eigenen Benutzer-Account. Dieser Account repräsentiert Sie als Person in unserer virtuellen Gemeinschaft.\r\nDamit nur Sie Zugriff auf Ihren Account haben, werden zum Einloggen Ihre E-Mail-Adresse und das dazugehörige Passwort benötigt.\r\nNach der Registrierung müssen Sie Ihren Account aktivieren, indem Sie einen Link aufrufen, den wir Ihnen per E-Mail zusenden. Damit bestätigen Sie sowohl die Echtheit als auch den Besitz über die angegebene Adresse. Sollten sie nämlich Ihr Passwort vergessen, können wir Ihnen an Ihre E-Mail-Adresse eine Anleitung zum Zurücksetzen des Passworts zusenden.\r\n\r\nFür die Sicherheit Ihres Accounts sind in erster Linie Sie selbst verantwortlich. Aus diesem Grund sollten Sie ein starkes Passwort (mindestens 13 Zeichen, Groß- und Kleinbuchstaben sowie Ziffern und Sonderzeichen) verwenden und es niemandem zugänglich machen.\r\n\r\nWeiterhin benötigen wir einen Anzeigenamen für Sie, über den die anderen Nutzer unserer Community Sie eindeutig identifizieren können. Dieser Nutzername pseudonymisiert Sie, sodass Sie Ihren realen Namen nicht preisgeben müssen.\r\n\r\nRechtsgrundlage für die Verarbeitung der Account-Daten ist Art. 6 Abs. 1 lit. a der DSGVO, da Sie uns bei der Account-Erstellung Ihre Einwilligung erteilen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_personal_event_heading', 'tbd', 'Teilnehmerdaten für Veranstaltungen', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_personal_event_paragraph', 'tbd', 'Diese Kategorie umfasst den Vornamen und Nachnamen, die Straße und Hausnummer, die Postleitzahl und den Wohnort, das Land, die Telefonnummer, das Geburtsdatum und die Essgewohnheiten.\r\n\r\nWenn Sie sich für eine unserer Veranstaltungen anmelden wollen, benötigen wir bei Ihrer ersten Anmeldung trotz der bereits getätigten Account-Registrierung noch weitere Daten. Ab der zweiten Anmeldung benötigen Sie zum Einschreiben nur noch einen einzigen Klick und müssen nicht erneut Ihre Daten angeben.\r\n\r\nFür die Anmeldung benötigen wir in erster Linie Ihre Kontaktdaten. Dazu zählen Vorname, Nachname, Straße, Hausnummer, Postleitzahl, Wohnort und Staat.\r\nNach den Veranstaltungen senden wir Ihnen nämlich gegebenenfalls einen virtuellen Rundbrief per E-Mail zu, der einen Rückblick auf die stattgefundene Veranstaltung enthält.\r\nWenn Sie zusätzlich noch eine physische Ausfertigung per Post erhalten wollen, können Sie uns dies durch das Setzen eines Häkchens in Ihrer Account-Übersicht mitteilen.\r\n\r\nZusätzlich benötigen wir Ihre Telefonnummer, um Sie bei dringenden Anliegen bezüglich der Veranstaltung zügig erreichen zu können.\r\n\r\nFür die Verifikation der Alterbeschränkung bestimmter Veranstaltungen benötigen wir Ihr Geburtsdatum.\r\n\r\nDa manche Veranstaltungen mehrere Tage dauern, sorgen wir in dieser Zeit für Ihre Verpflegung. Um den Essensplan bereits im Vorfeld auf die Teilnehmer abstimmen zu können, benötigen wir Informationen über Ihre Essgewohnheiten. Dazu zählen beispielsweise Nahrungsmittelunverträglichkeiten oder eine vegetarische/vegane Lebensweise.\r\n\r\nRechtsgrundlage für die Verarbeitung der Teilnehmerdaten ist Art. 6 Abs. 1 lit. a der DSGVO, da Sie uns Ihre Daten freiwillig für die Verarbeitung zum angegebenen Zweck zur Verfügung stellen und damit Ihre Einwilligung erteilen.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_consents', 'tbd', 'Einwilligungen', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_consents_mail_heading', 'tbd', 'E-Mail-Kommunikation', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_consents_mail_paragraph', 'tbd', 'Die Grundlage für unseren E-Mail-Versand ist Art. 6 Abs. 1 lit. b der DSGVO, der die Verarbeitung von Daten zur Erfüllung eines Vertrags gestattet.\r\nAus diesem Grund sind wir auch ohne Ihre Einwilligung berechtigt, Ihnen E-Mails zu senden, welche für die Nutzung unserer Plattformen notwendig sind. Dazu zählt beispielsweise die E-Mail mit dem Aktivierungslink des Accounts.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_type_consents_pn_heading', 'tbd', 'Push-Benachrichtungen', 'ANDROID+IOS+WEB'), ('privacy_policy_data_type_consents_pn_paragraph', 'tbd', 'Wenn Sie Push-Benachrichtigungen von unserer Web-App empfangen möchten, können Sie dazu Ihre Einwilligung beim Einloggen in Ihren Account erteilen. Sollten Sie später diese Einwilligung widerrufen wollen, ist dies in den Browser-Einstellungen möglich.\r\n\r\nRechtsgrundlage hierfür ist Art. 6 Abs. 1 lit. a der DSGVO.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_duration_heading', 'tbd', 'Wie lange werden Ihre Daten gespeichert?', 'ANDROID+IOS+WEB'), ('privacy_policy_data_duration_paragraph', 'tbd', 'Ihre Daten werden solange gespeichert, bis Sie Ihren Account löschen, Ihre Einwilligung zur Speicherung widerrufen, der mit der Speicherung verfolgte Zweck nicht mehr erforderlich ist oder ihre Speicherung aus sonstigen gesetzlichen Gründen unzulässig ist.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_data_rights_heading', 'tbd', 'Welche Rechte haben Sie bezüglich Ihrer Daten?', 'ANDROID+IOS+WEB'), ('privacy_policy_data_rights_paragraph', 'tbd', 'Sie haben jederzeit das Recht, unentgeltlich Auskunft über Herkunft, Empfänger, Zweck, geplante Speicherdauer und die Verarbeitungslogik Ihrer gespeicherten personenbezogenen Daten zu erhalten.\r\n\r\nEs besteht zudem die Möglichkeit, die gespeicherten personenbezogenen Daten über Ihre Person in strukturierter, gängiger und maschinenlesbarer Form zu erhalten.\r\n\r\nSind die gespeicherten Daten unrichtig, so können Sie ihre Berichtigung verlangen.\r\n\r\nSie haben außerdem ein Recht, die Sperrung der Daten oder die Einschränkung der Datenverarbeitung zu verlangen.\r\n\r\nDie Löschung der gespeicherten personenbezogenen Daten erfolgt, wenn Ihr Account gelöscht wird. \r\n\r\nHierzu sowie zu weiteren Fragen zum Thema Datenschutz können Sie sich jederzeit unter der im Impressum angegebenen Adresse an uns wenden. Des Weiteren steht Ihnen ein Beschwerderecht bei der zuständigen Aufsichtsbehörde zu.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical', 'tbd', 'Technische Aspekte', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_server_heading', 'tbd', 'Server', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_server_paragraph', 'tbd', 'Der Server wird von der Host Europe Group bereitgestellt.\r\nEr befindet sich im Kölner Rechenzentrum des Unternehmens.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_security', 'tbd', 'Sicherheit', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_security_encryption', 'tbd', 'Verschlüsselung', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_security_encryption_https_heading', 'tbd', 'Kommunikation mit der Website per HTTP(s)', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_security_encryption_https_paragraph', 'tbd', 'Die Kommunikation zwischen Ihrem Internet-Browser und unserer Plattform wird mit dem gängigen SSL/TLS-Verfahren verschlüsselt.\r\nEine verschlüsselte Verbindung ist daran zu erkennen, dass die Adresszeile des Browsers mit „https://“ beginnt und ein grünes Schloss-Symbol neben der Adressleiste zu sehen ist.\r\nWenn die SSL/TLS-Verschlüsselung aktiviert ist, können die Daten, die Sie an uns beziehungsweise wir an Sie übermitteln, nicht von Dritten mitgelesen werden.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_security_encryption_mail_heading', 'tbd', 'Kommunikation per E-Mail', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_security_encryption_mail_paragraph', 'tbd', 'Inhalte, die Sie unverschlüsselt per E-Mail an uns senden, können auf dem Übertragungsweg von Dritten gelesen und modifiziert werden.\r\nSollten Sie personenbezogene oder andere kritische Daten per E-Mail an uns senden wollen, empfehlen wir die verschlüsselte Kommunikation mittels OpenPGP.\r\nTutorials zur Nutzung von OpenPGP können im Internet gefunden werden. Die öffentlichen Schlüssel zu den Mail-Accounts unserer Domain sind auf den OpenPGP-Zertifikatsservern veröffentlicht oder können per E-Mail von uns angefordert werden. Wenn wir Ihren öffentlichen Schlüssel kennen, werden wir Ihnen ebenfalls verschlüsselt antworten. Dies gilt nicht für automatisch vom System generierte und versandte E-Mails.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_security_access_heading', 'tbd', 'Datenzugang', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_security_access_paragraph', 'tbd', 'Zugang zu den gespeicherten personenbezogenen Daten haben nur sehr wenige Personen.\r\nDies sind lediglich die Entwickler und Administratoren/Moderatoren.\r\n\r\nDie Passwörter der Benutzer-Accounts werden zudem bereits vor dem Speichern in die Datenbank verschlüsselt und können deshalb von niemandem eingesehen werden.\r\n\r\nDie Datenbank selbst ist mit sehr sicheren Anmeldedaten geschützt, sodass Unbefugten der Zugang versagt ist.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_transfer', 'tbd', 'Datenweitergabe aufgrund verwendeter Bibliotheken', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_transfer_google_maps_heading', 'tbd', 'Google Maps', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_transfer_google_maps_paragraph', 'tbd', 'Wir setzen auf unserer Plattform die Komponente \"Google Maps\" der Firma Google Inc., 1600 Amphitheatre Parkway, Mountain View, CA 94043 USA ein.\r\n\r\nBei jedem Aufruf der Komponente \"Google Maps\" wird von Google ein Cookie gesetzt, um bei der Anzeige der Seite, auf der die Komponente \"Google Maps\" integriert ist, Nutzereinstellungen und -daten zu verarbeiten. Dieses Cookie wird im Regelfall nicht durch das Schließen des Browsers gelöscht, sondern läuft nach einer bestimmten Zeit ab, soweit es nicht von Ihnen zuvor manuell gelöscht wird. Wenn Sie mit dieser Verarbeitung Ihrer Daten nicht einverstanden sind, so besteht die Möglichkeit, die Speicherung von Cookies in Ihrem Internet-Browser zu deaktivieren. Wir weisen Sie jedoch darauf hin, dass Sie in diesem Fall \"Google Maps\" nicht oder nur eingeschränkt nutzen können. \r\nWeiterhin wird Ihre IP-Adresse an die Server von Google übertragen und dort gespeichert. Wir haben keinen Einfluss auf diese Datenverarbeitung.\r\n\r\nDie Nutzung von Google Maps erfolgt für eine leichtere Auffindbarkeit der von uns angegebenen Orte. Dies stellt ein berechtigtes Interesse im Sinne von Art. 6 Abs. 1 lit. f DSGVO dar.\r\n\r\nDie Nutzung von \"Google Maps\" und der über \"Google Maps\" erlangten Informationen erfolgt gemäß den Google-Nutzungsbedingungen www.google.de/intl/de/policies/terms/regional.html sowie der zusätzlichen Geschäftsbedingungen für \"Google Maps\" https://www.google.com/intl/de_de/help/terms_maps.html.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_transfer_google_fonts_heading', 'tbd', 'Google Web Fonts', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_transfer_google_fonts_paragraph', 'tbd', 'Unsere Plattform nutzt zur einheitlichen Darstellung von Schriftarten so genannte Web Fonts, die von Google bereitgestellt werden. Beim Aufruf einer Seite lädt Ihr Browser die benötigten Web Fonts in ihren Browsercache, um Texte und Schriftarten korrekt anzuzeigen. Zu diesem Zweck muss der von Ihnen verwendete Browser Verbindung zu den Servern von Google aufnehmen. Hierdurch erlangt Google Kenntnis darüber, dass über Ihre IP-Adresse unsere Website aufgerufen wurde. Wenn Ihr Browser Web Fonts nicht unterstützt, wird eine Standardschrift von Ihrem Computer genutzt.\r\n\r\nDie Nutzung von Google Web Fonts erfolgt im Interesse einer einheitlichen und ansprechenden Darstellung unserer Plattform. Dies stellt ein berechtigtes Interesse im Sinne von Art. 6 Abs. 1 lit. f DSGVO dar.\r\n\r\nWeitere Informationen zu Google Web Fonts finden Sie unter https://developers.google.com/fonts/faq und in der Datenschutzerklärung von Google: https://www.google.com/policies/privacy/.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_transfer_firebase_messaging_heading', 'tbd', 'Firebase Cloud Messaging', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_transfer_firebase_messaging_paragraph', 'tbd', 'Wir setzen in unserer Web-App die Komponente „Firebase Cloud Messaging“ der Firma Google Inc., 1600 Amphitheatre Parkway, Mountain View, CA 94043 USA, nachfolgend \"Google\", ein.\r\n\r\nBeim ersten Starten der App wird eine eindeutige Kennnummer (Geräte-ID) an den Dienst übertragen. Der Dienst liefert einen sogenannten „Push Notification Identifier“ zurück, der keine Rückschlüsse auf Sie als Nutzer zulässt. Die nachfolgende Kommunikation erfolgt ausschließlich mit diesem Identifier. Aus diesem Grund wird die Geräte-ID von unserer App nicht weiter verwendet. Der Identifier wird solange gespeichert, wie Sie die Push-Benachrichtigungen in Anspruch nehmen, also bis zum Ausloggen aus dem Account, der Deaktivierung der Push-Benachrichtigungen in den App-Einstellungen des Betriebssystems oder der Deinstallation der App.\r\n\r\nRechtsgrundlage hierfür ist Art. 6 Abs. 1 lit. b der DSGVO.', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_technical_transfer_npm_packages_heading', 'tbd', 'NPM-Packages', 'ANDROID+IOS+WEB'), ('privacy_policy_technical_transfer_npm_packages_paragraph', 'tbd', 'Des Weiteren werden von unserer Web-App diverse NPM-Packages als Module genutzt, um vorgefertigte Lösungen, zum Beispiel für bestimmte visuelle Darstellungen oder die Netzwerkkommunikation, nicht selbst implementieren zu müssen.\r\nDie komplette Liste der verwendeten Packages wird auf Anfrage vom Entwickler herausgegeben.\r\n\r\nRechtsgrundlage hierfür ist Art. 6 Abs. 1 lit. b der DSGVO.', 'ANDROID+IOS+WEB');
