INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('privacy_policy_objection_heading', 'tbd', 'Widerspruch gegen Werbe-E-Mails', 'ANDROID+IOS+WEB'), ('privacy_policy_objection_paragraph', 'tbd', 'Der Nutzung von im Rahmen der Impressumspflicht veröffentlichten Kontaktdaten zur Übersendung von nicht ausdrücklich angeforderter Werbung und Informationsmaterialien wird hiermit widersprochen.\r\nDie Betreiber der Seiten behalten sich ausdrücklich rechtliche Schritte im Falle der unverlangten Zusendung von Werbeinformationen, etwa durch Spam-E-Mails, vor.', 'ANDROID+IOS+WEB');