INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('changelog_upcoming_paragraph', 'Interactivity\r\n> comment function (for news, events etc.)\r\n> profile pages\r\n> messenger\r\n> ride-share service platform\r\n> file up- and download\r\n> survey/feedback system\r\n\r\nNews\r\n> news channels to subscribe\r\n\r\nPush Notifications\r\n> secret update :x', 'Interaktivität\r\n> Kommentarfunktion (für Veranstaltungen, Neuigkeiten etc.)\r\n> Profilseiten\r\n> Messenger\r\n> Mitfahrbörse\r\n> Datei Up- und Download\r\n> Umfrage-/Feedback-System\r\n\r\nNeuigkeiten\r\n> Neuigkeitskanäle zum Abonnieren\r\n\r\nPush-Benachrichtigungen\r\n> Erweiterung um geheime Funktionalität :x', 'ANDROID+IOS+WEB');
INSERT INTO `strings` (`identifier`, `en`, `de`, `mode`) VALUES ('changelog_1_0_0', '+ added extensive account system\r\n+ added news list and page with general information and image gallery\r\n+ added event list and page with general information, enrollment page, image gallery and location information\r\n+ added push notifications\r\n+ added administration panel for editors and moderators\r\n+ added a ton of other things like legal texts, installation instructions, changelog und much more', '+ umfassendes Account-System hinzugefügt\r\n+ Neuigkeitsliste und -seite mit allgemeinen Informationen und Bildergalerie hinzugefügt\r\n+ Veranstaltungsliste und -seite mit allgemeinen Informationen, Anmeldeseite, Bildergalerie und Anfahrtsübersicht hinzugefügt\r\n+ Push-Benachrichtigungen hinzugefügt\r\n+ Administrationspanel für Redaktoren und Moderatoren hinzugefügt\r\n+ viele andere Dinge wie beispielsweise Rechtstexte, Installationsanleitungen, Änderungsprotokoll und vieles mehr hinzugefügt', 'ANDROID+IOS+WEB');
